var ss = SpreadsheetApp.openByUrl("https://docs.google.com/spreadsheets/d/1eOyXeIaGwOxGXWs3o484TClMEtt1LSdAe4379b9nzVo/edit#gid=111898187");
var sheet = ss.getSheetByName("barcode_system");

function doGet(e){
  var action  = e.parameter.action;

  if(action == "register")
    return registerTime(e);

  if(action == "lunch1")
    return lunchTime(e);

    if(action == "lunch2")
    return lunch1Time(e);

    if(action == "lunch3")
    return lunch2Time(e);

    if(action == "pizza")
    return pizzaTime(e);
}


