function registerTime(e){
  var id = e.parameter.id;
  var values = sheet.getRange(2,1,sheet.getLastRow(),1).getValues();

  for(var i = 0 ; i<values.length ; i++){
    if(values[i][0] == id){
      i=i+2;
      var register_time = Utilities.formatDate(new Date(), "GMT+03:00", "HH:mm:ss");              
      var value = sheet.getRange(i,4);
      if(value.isBlank()){
        sheet.getRange(i,4).setValue(register_time);
      return ContentService.createTextOutput("Doğrulama Başarılı").setMimeType(ContentService.MimeType.TEXT);


      }
      else{      
return ContentService.createTextOutput("Doğrulama Başarısız").setMimeType(ContentService.MimeType.TEXT);


      }
    }
  }
  return ContentService.createTextOutput("Id Bulunamadı").setMimeType(ContentService.MimeType.TEXT);
}


